import React, { useState,useRef} from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const baseURL = "https://account-management-system-hero.herokuapp.com/add-organization"
const AddOrganisation = (props) => {

    const navigate = useNavigate();
    // let orgid = null; fixed stmt
    //let orgid = 220; //for debugging purpose
    const orgid=null;

    const notify = () => toast.success("Organization added successfully!", {
        position: "top-right",
        autoClose: 12000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
    });

    const [organizationData, setOrganizationData] = useState({
        organization_name: "",
        organization_address: "",
    })
    //to prevent renders on every letter input by user
    const organization_name=useRef(null);
    const organization_address=useRef(null);

    const registerOrg = async (e) =>{
        e.preventDefault();
        const newOrgData={
            organization_name:organization_name.current.value,
            organization_address:organization_address.current.value
        }

        console.log("newdata"+newOrgData.organization_name)
        console.log("newOrgData"+organizationData.organization_name)
        await axios.post(baseURL,{
            organizationName : organizationData.organization_name,
            organizationAddress : organizationData.organization_address
        })
        .then(response=>{
            orgid =  response.data.organizationId;
             console.log(response.data.organizationId);//for debuggibg purpose
        })
        .catch(err =>{
            console.log("error connecting"+err.response);
        })

        navigate("/dash-board", {state : {id : orgid, name : "rakesh"}});
    }
    console.log(orgid); //for debugging purpose

    return (
        <>
            <div className="container mt-3">
                <div className="mb-3">
                    <h2 className="text-center">Register Organization</h2>
                </div>
                <form  id="orgForm" onSubmit={registerOrg}>
                    <div className="form-group">
                        <label htmlFor="organization_name">Organization Name </label>
                        <input 
                            type="text" 
                            className="form-control" 
                            name="organization_name" 
                            placeholder="Enter here"
                            ref={organization_name}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="organization_address">Organization Address</label>
                        <textarea 
                            className="form-control" 
                            name="organization_address" 
                            rows="3"
                            ref={organization_address}
                            required
                            />
                    </div>                    
                        <button 
                            type="submit" 
                            className="btn btn-primary"
                            onClick={notify}
                        >
                            Register
                        </button>
                </form>
                <ToastContainer />
            </div>
        </>
    )
}

export default AddOrganisation
