        pauseOnHover: true,
        draggable: true,
        progress: undefined,
    });

    const [organizationData, setOrganizationData] = useState({
        organization_name: "",
        organization_address: "",
    })
    const organization_name=useRef();
    const organization_address=useRef();
    const addOrganization = (e) => {
