        //      console.log(response.data.organizationId);//for debuggibg purpose
        // })
        // .catch(err =>{
        //     console.log("error connecting"+err.response);
        // })

        //navigate("/dash-board", {state : {id : orgid, name : "rakesh"}});
    }
    console.log(orgid); //for debugging purpose




    //form validatation
    /*
    document.addEventListener('DOMContentLoaded', function  (e) {
        const form = document.getElementById('orgForm');
        const fv = FormValidation.formValidation(form, {
            fields:{
                'organization_name' : {
                    validators: {

                    }
                }
            }
        })

    }) */
    return (
        <>
            <div className="container mt-3">
                <div className="mb-3">
                    <h2 className="text-center">Register Organization</h2>
                </div>
                <form method="post" action="/dash-board" id="orgForm" onSubmit={()=>{registerOrg(e)}}>
                    <div className="form-group">
                        <label htmlFor="organization_name">Organization Name </label>
                        <input 
                            type="text" 
                            className="form-control" 
                            name="organization_name" 
                            placeholder="Enter here"
                            ref={organization_name}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="organization_address">Organization Address</label>
                        <textarea 
                            className="form-control" 
                            name="organization_address" 
                            rows="3"
                            ref={organization_address}
                            required
                            />
                    </div>                    
                        <button 
                            type="submit" 
                            className="btn btn-primary"
                            onClick={notify}
                        >
                            Register
                        </button>
                </form>
                <ToastContainer />
            </div>
        </>
    )
}

export default AddOrganisation
