import React, { useState,useRef} from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const baseURL = "https://account-management-system-hero.herokuapp.com/add-organization"
const AddOrganisation = (props) => {

    const navigate = useNavigate();
    // let orgid = null; fixed stmt
    //let orgid = 220; //for debugging purpose
    const orgid=12;

    const notify = () => {toast.success("Organization added successfully!", {
        position: "top-right",
        autoClose: 12000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
    });
    const newOrgData = {...organizationData}
        newOrgData[e.target.name] = e.target.value
        setOrganizationData(newOrgData)
}

    const [organizationData, setOrganizationData] = useState({
        organization_name: "",
        organization_address: "",
    })
    //to prevent renders on every letter input by user
    const org_name=useRef(null);
    const org_addr=useRef(null);

    const addOrganization = (e) => {
        const newOrgData = {...organizationData}
        newOrgData[e.target.name] = e.target.value
        setOrganizationData(newOrgData)
        //console.log(newOrgData); //for debugging purpose
    }
    const registerOrg = async (e) =>{
        e.preventDefault();

        const newOrgData = {...organizationData}
        newOrgData["organization_name"]=org_name.current.value;
        newOrgData["organization_address"]=org_addr.current.value;
        setOrganizationData(newOrgData)
        console.log("newdata"+org_name.current.value)
        console.log("newOrgData"+Object.values(organizationData))
        await axios.post(baseURL,{
            "organizationName" :  newOrgData["organization_name"],
            "organizationAddress" : newOrgData["organization_address"]
        })
        .then((res)=>res.json())
        .then((data)=>{
            orgid =  data.organizationId;
             console.log(data.organizationId);//for debuggibg purpose
        })
        .catch(err =>{
            console.log("error connecting"+err.response);
        })

        navigate("/dash-board", {state : {id : orgid, name : "rakesh"}});
    }
    console.log(orgid); //for debugging purpose

    return (
        <>
            <div className="container mt-3">
                <div className="mb-3">
                    <h2 className="text-center">Register Organization</h2>
                </div>
                <form method="post" action="/dash-board" id="orgForm" onSubmit={registerOrg}>
                    <div className="form-group">
                        <label htmlFor="organization_name">Organization Name </label>
                        <input 
                            type="text" 
                            className="form-control" 
                            name="organization_name" 
                            placeholder="Enter here"
                            ref={org_name}

                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="organization_address">Organization Address</label>
                        <textarea 
                            className="form-control" 
                            name="organization_address" 
                            rows="3"
                            ref={org_addr}
                            required
                            />
                    </div>                    
                        <button 
                            type="submit" 
                            className="btn btn-primary"
                            onClick={notify}
                        >
                            Register
                        </button>
                </form>
                <ToastContainer />
            </div>
        </>
    )
}

export default AddOrganisation
